            elements = (T[]) list.toArray();
        }
    
        public Iterator&lt;List&lt;T&gt;&gt; iterator() {
            return new Iterator&lt;List&lt;T&gt;&gt;() {
    
                int pos = 0;
    
                public boolean hasNext() {
                    return cGenerator.hasMore();
                }
    
                public List&lt;T&gt; next() {
                    if (!hasNext()) {
                        throw new NoSuchElementException();
                    }
                    indices = cGenerator.getNext();
                    List&lt;T&gt; combination = new ArrayList&lt;T&gt;();
                    for (int i = 0; i &lt; indices.length; i++) {
                        combination.add(elements[indices[i]]);
                    }
                    return combination;
                }
    
                public void remove() {
                    throw new UnsupportedOperationException();
                }
            };
        }
    
        private final class CombinationGenerator {
    
            private int[] a;
            private int n;
            private int r;
            private BigInteger numLeft;
            private BigInteger total;
    
            //------------
            // Constructor
            //------------
            public CombinationGenerator(int n, int r) {
                if (n &lt; 1) {
                    throw new IllegalArgumentException(&quot;Set must have at least one element&quot;);
                }
                if (r &gt; n) {
                    throw new IllegalArgumentException(&quot;Subset length can not be greater than set length&quot;);
                }
                this.n = n;
                this.r = r;
                a = new int[r];
                BigInteger nFact = getFactorial(n);
                BigInteger rFact = getFactorial(r);
                BigInteger nminusrFact = getFactorial(n - r);
                total = nFact.divide(rFact.multiply(nminusrFact));
                reset();
            }
    
            //------
            // Reset
            //------
            public void reset() {
                for (int i = 0; i &lt; a.length; i++) {
                    a[i] = i;
                }
                numLeft = new BigInteger(total.toString());
            }
    
            //------------------------------------------------
            // Return number of combinations not yet generated
            //------------------------------------------------
            public BigInteger getNumLeft() {
                return numLeft;
            }
    
            //-----------------------------
            // Are there more combinations?
            //-----------------------------
            public boolean hasMore() {
                return numLeft.compareTo(BigInteger.ZERO) == 1;
            }
    
            //------------------------------------
            // Return total number of combinations
            //------------------------------------
            public BigInteger getTotal() {
                return total;
            }
    
            //------------------
            // Compute factorial
            //------------------
            private BigInteger getFactorial(int n) {
                BigInteger fact = BigInteger.ONE;
                for (int i = n; i &gt; 1; i--) {
                    fact = fact.multiply(new BigInteger(Integer.toString(i)));
                }
                return fact;
            }
    
            //--------------------------------------------------------
            // Generate next combination (algorithm from Rosen p. 286)
            //--------------------------------------------------------
            public int[] getNext() {
    
                if (numLeft.equals(total)) {
                    numLeft = numLeft.subtract(BigInteger.ONE);
                    return a;
                }
    
                int i = r - 1;
                while (a[i] == n - r + i) {
                    i--;
                }
                a[i] = a[i] + 1;
                for (int j = i + 1; j &lt; r; j++) {
                    a[j] = a[i] + j - i;
                }
    
                numLeft = numLeft.subtract(BigInteger.ONE);
                return a;
    
            }
        }
    }

