    private static RetryPolicy retryPolicy = new RetryPolicy()
        .withMaxRetries(MAX_RETRIES);

    public CompletableFuture&lt;Result&gt; executeActionAsync() {
        return Failsafe.with(retryPolicy)
            .with(executor)
            .withFallback(null)
