        }
        else if (l &lt; tiles[0].length - 1) {
          findSolutions(newTiles, newCount, i, l + 1);
        }
        else if (l == tiles[0].length - 1 &amp;&amp; i == tiles.length - 1) {
          solutions.add(newTiles);
