    Root&lt;Queue&gt; queueRoot = criteria.from(Queue.class);
    criteria.where(
        builder.and(
            builder.equal(queueRoot.get(&quot;location&quot;).get(&quot;uuid&quot;), location_uuid),
            builder.equal(queueRoot.get(&quot;queueRoom&quot;).get(&quot;uuid&quot;), room_uuid)
        )
    );

