    public static void WriteBuffer(DataOutputStream os, byte[] array) throws IOException {
            if ((array!=null) &amp;&amp; (array.length &gt; 0) &amp;&amp; (array.length &lt;= MAX_BUFFER_SIZE)) {
                WriteInt(os,array.length);
                os.write(array);
