               + &quot; CurrentLocation(x,y,z): &quot; + loc.getBlockX() + &quot; &quot; + loc.getBlockY() + &quot; &quot; + loc.getBlockZ() + &quot;\n&quot;;
  
       File file = new File(dataFolder, currentPlayer.getName() + &quot;.log&quot;);
