            while (clip.getFramePosition() &lt; clip.getFrameLength()) {
                Thread.yield();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        outerLatch.countDown();

    }

    private URL getUrl(final File file) {
        try {
            return file.toURI().toURL();
        } catch (MalformedURLException e) {
            e.printStackTrace();
            return null;
        }
    }

}
