             synchronized (this) {
                 if(!printEvenNumber) {
                     try {
                         wait();
                     } catch (InterruptedException e) {
                         e.printStackTrace();
                     }
                 }
                 System.out.println(i);
                 printEvenNumber = !printEvenNumber;
                 notify();
