        int n = input.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        System.out.println(&quot;Please enter number of trucks using a road over the &quot; + n + &quot; day period: &quot;);
        for (int i = 0; i &lt; n; i++) {
            a[i] = input.nextInt();
            sum += a[i];
        }
        if (n != 0) mean = sum / n;
        System.out.println(&quot;The mean is: &quot; + mean);
        printHeaders(&quot;Sorted in ascending order&quot;);
        for (int i = 0; i &lt; n; i++) {
            for (int j = i + 1; j &lt; n; j++) {
                sort(a[i] &gt; a[j], a, b, i, j);
            }
        }
        calculateFrequency(n, a, b);
        printValues(n, a, b);
        printHeaders(&quot;Sorted in descending order&quot;);
        for (int i = 0; i &lt; n; i++) {
            for (int j = i + 1; j &lt; n; j++) {
                sort(a[i] &lt; a[j], a, b, i, j);
            }
        }
        printValues(n, a, b);
        ArrayLength(a);
    }
    private static void calculateFrequency(int n, int[] a, int[] b) {
        for (int i = 0; i &lt; n; i++) {
            int c = 1;
            for (int j = i + 1; j &lt; n; j++) {
                if (a[i] != a[j]) continue;
                c = c + 1;
                a[j] = -1;
            }
            b[i] = c;
        }
    }
