    @RunWith(AllTests.class)
    public class DynamicTestSuite {
        public static TestSuite suite() {
            TestSuite suite = new TestSuite();

            // dynamically create your test case here  &lt;====
            suite.addTest(new MyTestCase());

            return suite;
        }
    }

