public class Solution {  
    public static void main(String args[]) {
        int total = 0;
        HashMap&lt;String,String&gt; a = new HashMap&lt;String,String&gt;();
        a.put(&quot;1.&quot;,&quot;1&quot;);
        a.put(&quot;2.&quot;,&quot;11&quot;);
        a.put(&quot;3.&quot;,&quot;21&quot;);
        a.put(&quot;4.&quot;,&quot;1211&quot;);
