        }
        // For checking if the number is in the box
        int boxRow = row - row % 3;
        int boxCol = col - col % 3;
        for (int i = boxRow; i &lt; boxRow + 3; i++) {
            for (int j = boxCol; j &lt; boxCol + 3; j++) {
