        &quot;name&quot;: &quot;Launch Dev AppServer&quot;,
        &quot;request&quot;: &quot;launch&quot;,
        &quot;mainClass&quot;: &quot;AppServer&quot;,
        &quot;projectName&quot;: &quot;App&quot;,
