    {
        public static void main (String[] args)
        {
            int x = 10;
            System.out.println((x++ % 2 == 0) ? &quot;yes &quot; + x: &quot; no &quot; + x);
            x = 10;
            System.out.println((++x % 2 == 0) ? &quot;yes &quot; + x: &quot; no &quot; + x);
        }
