    	}
    
    	@Override
    	public void putAll(Map&lt;? extends K, ? extends V&gt; m) {
    		for (K key : m.keySet()) {
    			put(key, m.get(key));
    		}
    	}
    
    	@Override
    	public V putIfAbsent(K key, V value) {
    		if (!containsKey(key))
    			return put(key, value);
    		else
    			return get(key);
    	}
    
    	class CleanerThread extends Thread {
    		@Override
