    public InputFile(String dataFilePath) {
        this.dataFilePath = dataFilePath;
        initReader();
        
    }
    
    private void initReader() {
        try {
            reader = new Scanner(new File(dataFilePath));
        }
        catch (FileNotFoundException ex) {
            JOptionPane.showMessageDialog(null, &quot;Data File Not Found!&quot;, 
                        &quot;File Not Found!&quot;, JOptionPane.WARNING_MESSAGE);
        }
    }
    
    public boolean eof() {
        if (reader.hasNextLine()) {
            return false;
        }
        else {
            reader.close();
            return true;
        }
    }
    
    public String readString() {
        String line = reader.nextLine().trim();
        if (line.startsWith(&quot;;&quot;) || line.isEmpty()) {
            line = &quot;&quot;;
        }
        return line;
    }
    
    public String readWord() {
        String word = reader.next().trim();
        if (word.startsWith(&quot;;&quot;) || word.isEmpty()) {
            word = &quot;&quot;;
        }
        return word;
    }
    
    public int readInt() {
        int val = 0;
        try {
            val = reader.nextInt();
            return val;
        }
        catch (Exception ex) {
            return 0;
        }
    }
    
    public long readLong() {
        long val = 0L;
        try {
            val = reader.nextLong();
            return val;
        }
        catch (Exception ex) {
            return 0L;
        }
    }

    public double readDouble() {
