            return val;
        }
        catch (Exception ex) {
            return 0.0f;
        }
    }
    
    public void close() {
        reader.close();
    }

    public String getDataFilePath() {
        return dataFilePath;
    }

    public void setDataFilePath(String dataFilePath) {
        this.dataFilePath = dataFilePath;
    }
}
