    public void displayTable(double amount) {
        int paymentNumber;

        double payment = 0, interest, principal, balance;

        DecimalFormat numFormat;
        numFormat = new DecimalFormat(&quot;0.00&quot;);
        NumberFormat currencyFormat;
        currencyFormat = NumberFormat.getCurrencyInstance();
        balance = amount;
        paymentNumber = 0;
        double totalPayment = 0;
        double totalIntPaid = 0;
        while (balance &gt; 0.0d) {

            paymentNumber++;
            String dueDate = calcDueDate(month, day, year, paymentNumber);
            interest = calcInterest(balance, loanInterestRate);
            if (balance &lt;= payment) {
                payment = balance;
                principal = balance;
                balance = 0;
            }
            else {
                payment = calcPayment(loanPaymentCode, amount);
                principal = calcPrincipal(payment, interest);
                balance = calcBalance(balance, principal);
            }
            totalPayment = totalPayment + payment;
            totalIntPaid = totalIntPaid + interest;
            String paymentString = String.format(&quot;%8d %19s %13s %15s %15s %15s%n&quot;,
                    paymentNumber, dueDate, &quot;$&quot; + numFormat.format(payment),
                    currencyFormat.format(interest), currencyFormat.format(principal),
                    (balance == 0d ? &quot;(&quot; + currencyFormat.format(balance) + &quot;)&quot; : 
                            currencyFormat.format(balance)));
            jta.append(paymentString);

        }

        jta.append(LS);
        jta.append(String.format(&quot;%17s %10s %13s %15s%n&quot;, &quot; &quot;, &quot;Totals:&quot;,
                currencyFormat.format(totalPayment),
                currencyFormat.format(totalIntPaid)));
        jta.append(LS);

    }

    public static double calcPayment(int code, double amount) {
        double payment;
        payment = 0;

        switch (code) {
            case 0:
                payment = 50.00;
                break;
            case 1:
                payment = 55.00;
                break;
            case 2:
                payment = 75.00;
                break;
            case 3:
                payment = 100.00;
                break;
            case 4:
                payment = .05 * amount;
                break;
            case 5:
                payment = .06 * amount;
                break;
            case 6:
                payment = .05 * amount;
                break;
            case 7:
                payment = .04 * amount;
                break;
            case 8:
                payment = .03 * amount;
                break;
            case 9:
                payment = .02 * amount;
                break;
            default:
                System.out.println(&quot;Bad payment code&quot;);
        }
        return payment;
    }

    public static double calcInterest(double balance, double rate) {
        double interest;
        interest = (((rate / 12d) * balance) / 100.0d);
        return interest;
    }

    public static double calcPrincipal(double payment, double interest) {
        double principal;
        principal = payment - interest;
        return principal;
    }

    public static double calcBalance(double balance, double principal) {
        balance -= principal;
        return balance;
    }

    public static String calcDueDate(int month, int day, int year, int payNum) {
        String monthStr = &quot;&quot;;
        month = month + payNum - 1;
        year = year + (month / 12);
        month = month % 12;
        if (month == 0) {
            year--;
        }
        switch (month) {
            case 1:
                monthStr = &quot;Jan&quot;;
                break;
            case 2:
                monthStr = &quot;Feb&quot;;
                break;
            case 3:
                monthStr = &quot;Mar&quot;;
                break;
            case 4:
                monthStr = &quot;Apr&quot;;
                break;
            case 5:
                monthStr = &quot;May&quot;;
                break;
            case 6:
                monthStr = &quot;Jun&quot;;
                break;
            case 7:
                monthStr = &quot;Jul&quot;;
                break;
            case 8:
                monthStr = &quot;Aug&quot;;
                break;
            case 9:
                monthStr = &quot;Sep&quot;;
                break;
            case 10:
                monthStr = &quot;Oct&quot;;
                break;
            case 11:
                monthStr = &quot;Nov&quot;;
                break;
            case 0:
                monthStr = &quot;Dec&quot;;
                break;
            default:
                System.out.println(&quot;Invalid month: &quot; + month);

        }
        return monthStr + &quot; &quot; + day + &quot;, &quot; + year;
    }

    private void printReport() {
        PrintRequestAttributeSet attr = null;
        MessageFormat header = new MessageFormat(reportHeader);
        MessageFormat footer = new MessageFormat(reportFooter);
        jta.setFont(new Font(&quot;Monospaced&quot;, jta.getFont().getStyle(), 10));
        jf.setAlwaysOnTop(false);
        try {
            PrinterJob pj = PrinterJob.getPrinterJob();
            PageFormat pf = pj.defaultPage();
            Paper paper = new Paper();
            double margin = 0;

            // Left, Top, Right, Bottom 
            paper.setImageableArea(margin, margin, paper.getWidth() - 5.0d, paper.getHeight());
            pf.setPaper(paper);

            //pj.setPrintable(new MyPrintable(jta), pf);
            pj.setPrintable(jta.getPrintable(header, footer), pf);

            if (attr == null) {
                boolean portrait = true;
                Insets insets = new Insets(10, 10, 10, 10);
                attr = new HashPrintRequestAttributeSet();
                float leftMargin = insets.left;
                float rightMargin = insets.right;
                float topMargin = insets.top;
                float bottomMargin = insets.bottom;
                if (portrait) {
                    attr.add(OrientationRequested.PORTRAIT);
                }
                else {
                    attr.add(OrientationRequested.LANDSCAPE);
                    leftMargin = insets.top;
                    rightMargin = insets.bottom;
                    topMargin = insets.right;
                    bottomMargin = insets.left;
                }
                attr.add(MediaSizeName.NA_LETTER);
                MediaSize mediaSize = MediaSize.NA.LETTER;
                float mediaWidth = mediaSize.getX(Size2DSyntax.MM);
                float mediaHeight = mediaSize.getY(Size2DSyntax.MM);
                attr.add(new MediaPrintableArea(
                        leftMargin, topMargin,
                        (mediaWidth - leftMargin - rightMargin),
                        (mediaHeight - topMargin - bottomMargin), Size2DSyntax.MM));
            }
            
            boolean printComplete = false;
            if (pj.printDialog()) {
                pj.print(attr);
                printComplete = true;
            }

            if (printComplete) {
                JOptionPane.showMessageDialog(jta, &quot;You can pick up your report from the printer!&quot;,
                        &quot;Print Job Completered&quot;, JOptionPane.INFORMATION_MESSAGE);
            }
            else {
                JOptionPane.showMessageDialog(jta, &quot;Print Job Canceled!&quot;,
                        &quot;Print Canceled!&quot;, JOptionPane.INFORMATION_MESSAGE);
            }
        }
        catch (PrinterException ex) {
            java.util.logging.Logger.getLogger(LoanPaymentsReport.class.getName())
                                    .log(java.util.logging.Level.SEVERE, null, ex);
        }
        jta.setFont(new Font(&quot;Monospaced&quot;, jta.getFont().getStyle(), 12));
        jf.setAlwaysOnTop(true);
    }

    /* Inner class for a custom Printable for the JTextArea we will print.
       This class is used within the printReport() method only.       */
    class MyPrintable implements Printable {

        JTextArea str;

        MyPrintable(JTextArea getStr) {
            str = getStr;
        }

        @Override
        public int print(Graphics g, PageFormat pf, int pageIndex) {
            if (pageIndex != 0) {
                return NO_SUCH_PAGE;
