class NetworkAvailability(var context: Context) {
    @Suppress(&quot;DEPRECATION&quot;)
    fun isInternetAvailable(): Boolean {
        var result = false
        val cm = context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager?
        val currentnetwork = cm!!.activeNetwork
        if (Build.VERSION.SDK_INT &gt;= Build.VERSION_CODES.M) {
            cm.run {
                cm.getNetworkCapabilities(cm.activeNetwork)?.run {
                    result = when {
                        hasTransport(NetworkCapabilities.TRANSPORT_WIFI) -&gt; {
                            return cm.getNetworkCapabilities(currentnetwork)!!
                                    .hasCapability(NetworkCapabilities.NET_CAPABILITY_INTERNET) &amp;&amp; cm.getNetworkCapabilities(
                                    currentnetwork
                                )!!.hasCapability(NetworkCapabilities.NET_CAPABILITY_VALIDATED)


                        }
                        hasTransport(NetworkCapabilities.TRANSPORT_CELLULAR) -&gt; true
                        hasTransport(NetworkCapabilities.TRANSPORT_ETHERNET) -&gt; true
                        else -&gt; false
                    }
                }
            }
        } else {
            cm.run {
                cm.activeNetworkInfo?.run {
                    if (type == ConnectivityManager.TYPE_WIFI) {
                        result = true
                    } else if (type == ConnectivityManager.TYPE_MOBILE) {
                        result = true
                    }
                }
            }
        }
        return result
    }

    /**
     * @isInternetAvailableM(context)
     * this code for minimum sdk version is M
     * */
    @RequiresApi(Build.VERSION_CODES.M)
    fun isInternetAvailableM(): Boolean {
        val connectivityManager =
            context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager?
        val capability =
            connectivityManager!!.getNetworkCapabilities(connectivityManager.activeNetwork)
        return capability?.hasCapability(NetworkCapabilities.NET_CAPABILITY_INTERNET) ?: false

    }

}

