        var head = 0; 
        var tail = A.Length -1;
        var absCount = 1;
        var current = A[0] &gt; 0 ? A[0] : -A[0];
        while(head &lt;= tail)
        {
            var former = A[head] &gt; 0 ? A[head] : -A[head];
            if(former == current)
            {
                head ++;
                continue;
            }
            
            var latter = A[tail] &gt; 0 ? A[tail] : -A[tail];
            if(latter == current)
            {
                tail--;
                continue;
            }
        
            if(former &gt;= latter)
            {
                current = former;
                head ++;
            }
            else 
            {
                current = latter;
                tail--;
            
            }
            
            absCount++;
        }
        
        return absCount;
    }
}

