      useremail TEXT,
      PRIMARY KEY (userid)
    );
    CREATE TABLE IF NOT EXISTS meeting (
      meetingid INTEGER,
      meetingname TEXT,
      startdatetime integer,
      enddatetime integer,
      PRIMARY KEY (meetingid)
    );
    CREATE TABLE IF NOT EXISTS usermeetingmap (
      usermap LONG NOT NULL,
      meetingmap LONG NOT NULL,
      PRIMARY KEY (usermap, meetingmap),
      FOREIGN KEY (usermap) REFERENCES user (userid) ON DELETE CASCADE ON UPDATE CASCADE,
