    }

    static class Cloned implements Cloneable {

        public final Map&lt;String, String&gt; d = new HashMap&lt;&gt;();

        @Override
        @SneakyThrows
        public Cloned clone() {
            return (Cloned) super.clone();
        }
    }

}
