class Solution {
    public int peakIndexInMountainArray(int[] arr) {
        int low=0;
        int high=arr.length-1;
        int mid=0;
        while(low&lt;=high){
            mid = (low+high)/2;
            if (mid==0 | (arr[mid]&gt;=arr[mid-1]) &amp;&amp; (mid==high | arr[mid]&gt;=arr[mid+1]))
