      public static boolean equals(Matrix m1, Matrix m2) {
        if (m1.dimension != m2.dimension) {
          return false;
        }
        for (int i = 0; i &lt; m1.dimension; i++) {
          for (int j = 0; j &lt; m1.dimension; j++) {
