    private File createTempZipFile(MultipartFile file) throws IOException {
        File tempDir = Files.createTempDirectory(&quot;data&quot;).toFile();

        byte[] buffer = new byte[1024];

        try(ZipInputStream zis = new ZipInputStream(file.getInputStream())) {
            ZipEntry zipEntry;

            while ((zipEntry = zis.getNextEntry()) != null) {

                if (!zipEntry.isDirectory()) {
                    FileOutputStream fos = new FileOutputStream(new File(tempDir, zipEntry.getName()));

                    int len;
