```
 public int solution(int[] A) {
    Set&lt;Integer&gt; set = new HashSet&lt;Integer&gt;();
    for (int i = 0; i &lt; A.length; i++) {
      set.add(A[i]);
    }

    int max = 0, missing = -1;
    for (int i = 1; i &lt;= A.length; i++) {
      max = i;
      if (!set.contains(i)) {
        missing = i;
        break;
      }
    }
    return missing == -1 ? max + 1 : missing;
  }

