import java.util.*;

class Solution {
    public int solution(int[] A) {
        Arrays.sort(A);
        int i = 1;
        while (i &lt;= A.length) {
            int res = Arrays.binarySearch(A, i); 
            if (res &lt; 0) {
                return i;
            }
            i++;
        }
        return i;
    }
}
