    value = scriptPattern.matcher(value).replaceAll(&quot;&quot;);

    // Prohibit lines containing = (...) added
    scriptPattern = Pattern.compile(&quot;=(.*?)\\((.*?)\\)&quot;, Pattern.CASE_INSENSITIVE | Pattern.MULTILINE | Pattern.DOTALL);
    value = scriptPattern.matcher(value).replaceAll(&quot;&quot;);
    
