            &lt;executions&gt;
                &lt;execution&gt;
                    &lt;id&gt;delombok-sources&lt;/id&gt;
                    &lt;phase&gt;generate-sources&lt;/phase&gt;
                    &lt;goals&gt;
                        &lt;goal&gt;delombok&lt;/goal&gt;
                    &lt;/goals&gt;
