    private void RecursiveInitialize(object o,IList completed)
    {
        if (completed == null) throw new ArgumentNullException(&quot;completed&quot;);

        if (o == null) return;            

        if (completed.Contains(o)) return;            

        NHibernateUtil.Initialize(o);

        completed.Add(o);

