    public static ArrayList&lt;Integer&gt; getValuesAboveThreshold(int threshold) {

        // Output for values above 78: [85, 93, 81, 79, 81, 93]

        int[] a = new int[] { 58, 78, 61, 72, 93, 81, 79, 78, 75, 81, 93 };

        ArrayList&lt;Integer&gt; temp = new ArrayList&lt;&gt;();

