				break;
			case R.id.question5_radioButton5:
				if(checked) {score = score + 5;}
				break;
			case R.id.question5_radioButton6:
				if(checked) {score = score + 6;}
				break;
			case R.id.question5_radioButton7:
				if(checked) {score = score + 7;}
