     return((Any)((Float)(a+1)));
    }
    public &lt;Any&gt; Any getParam(Object b) {
     return((Any)b);
    }
    public void test(){
      boolean foo = getParam(true);
      float bar = getParam(1.0f);
      float mumble = getParam(this); // will get a class cast exception
