**Row Class** represent row data

  

    public class Row implements Comparable&lt;Row&gt; {

	private int number;
	private String country;
	private String animal;
	private String city;

	public Row(int number, String country, String animal, String city) {
		super();
		this.number = number;
		this.country = country;
		this.animal = animal;
		this.city = city;
	}

	public int getNumber() {
		return number;
	}

	public void setNumber(int number) {
		this.number = number;
	}

	public String getCountry() {
		return country;
	}

	public void setCountry(String country) {
		this.country = country;
	}

	public String getAnimal() {
		return animal;
	}

	public void setAnimal(String animal) {
		this.animal = animal;
	}

	public String getCity() {
		return city;
	}

	public void setCity(String city) {
		this.city = city;
	}

	// Easy to print and show the row data
	@Override
	public String toString() {
		return &quot;Row [number=&quot; + number + &quot;, country=&quot; + country + &quot;, animal=&quot;
				+ animal + &quot;, city=&quot; + city + &quot;]&quot;;
	}

	// sort based on column &quot;country&quot;
	@Override
	public int compareTo(Row o) {
		return this.country.compareTo(o.country);
	}

}

 


