    public static &lt;T&gt; ArrayList&lt;T[]&gt; chunks(ArrayList&lt;T&gt; bigList,int n){
		ArrayList&lt;T[]&gt; chunks = new ArrayList&lt;T[]&gt;();

		for (int i = 0; i &lt; bigList.size(); i += n) {
			T[] chunk = (T[])bigList.subList(i, Math.min(bigList.size(), i + n)).toArray();			
			chunks.add(chunk);
		}
		
