BlockingQueue&lt;QObj&gt; bq = new LinkedBlockingQueue&lt;&gt;();
List&lt;String&gt; idList = new LinkedList&lt;&gt;();
Object lockObj = idList;
int Idx = 1;

public static class QObj {
