        c.test(c);
    }
}

public boolean checkForClass(String className) {
    try  {
        Class.forName(className);
        return true;
    }  catch (ClassNotFoundException e) {
        return false;
    }
}

// somewhere in your code
if (checkForClass(&quot;package.DeletedClass&quot;)) {
    MyExtraClass.doStuff();
}
```

