            case D d -&gt; &quot;2&quot;;
            case E e -&gt; &quot;3&quot;;
            case F f -&gt; &quot;4&quot;;
            default  -&gt; &quot;5&quot;;
        };
    }

`main()`

    public static void main(String[] args) {
        System.out.println(foo(new E()));
        System.out.println(foo(new F()));
    }

