    float GpsToDecimalDegrees(const char* nmeaPos, char quadrant)
    {
      float v= 0;
      if(strlen(nmeaPos)&gt;5)
      {
        char integerPart[3+1];
        int digitCount= (nmeaPos[4]==&#39;.&#39; ? 2 : 3);
