      }
      return intval( $sign * floor(abs($n)) );
    }
    
    function ToInt32( $n ) {
      $sign = ( $n &lt; 0 ) ? -1 : 1;
    
      if ( abs( $n ) == 0 || abs( $n ) == INF) {
        return 0;
      }
    
      $n = ($sign * floor( abs($n) )) % pow(2,32);
      $n = ( $n &gt;= pow(2,31) ) ? $n - pow(2,32) : $n;
    
      return ( $n );
    }
    
