    &quot;&lt;/body&gt;&quot; +
    &quot;&lt;/html&gt;&quot;;
    
    Socket connectedClient = null;
    BufferedReader inFromClient = null;
    DataOutputStream outToClient = null;
    
    
    public myHTTPServer(Socket client) {
    connectedClient = client;
