package kdtree;

class KDNode{
    KDNode left;
    KDNode right;
    int []data;
    
    public KDNode(){
        left=null;
        right=null;
    }
    
    public KDNode(int []x){
        left=null;
        right=null;
        data = new int[2];
        for (int k = 0; k &lt; 2; k++)
            data[k]=x[k];
    }
}
class KDTreeImpl{
    KDNode root;
    int cd=0;
    int DIM=2;

    public KDTreeImpl() {
        root=null;
    }
    
    public boolean isEmpty(){
        return root == null;
    }
    
    public void insert(int []x){
        root = insert(x,root,cd);
    }
    private KDNode insert(int []x,KDNode t,int cd){
        if (t == null)
            t = new KDNode(x);
        else if (x[cd] &lt; t.data[cd])
            t.left = insert(x, t.left, (cd+1)%DIM);
        else
            t.right = insert(x, t.right, (cd+1)%DIM);
        return t;
    }
    
    public boolean search(int []data){
        return search(data,root,0);
    }
    
    private boolean search(int []x,KDNode t,int cd){
        boolean found=false;
        if(t==null){
            return false;
        }
        else {
            if(x[cd]==t.data[cd]){
                if(x[0]==t.data[0] &amp;&amp; x[1]==t.data[1]) 
                return true;
            }else if(x[cd]&lt;t.data[cd]){
                found = search(x,t.left,(cd+1)%DIM);
            }else if(x[cd]&gt;t.data[cd]){
                found = search(x,t.right,(cd+1)%DIM);
            }
            return found;
        }
    }
    
    public void inorder(){
        inorder(root);
    }
    private void inorder(KDNode r){
        if (r != null){
            inorder(r.left);
            System.out.print(&quot;(&quot;+r.data[0]+&quot;,&quot;+r.data[1] +&quot;) &quot;);
            inorder(r.right);
        }
    }
    public void preorder() {
        preorder(root);
    }
    private void preorder(KDNode r){
        if (r != null){
            System.out.print(&quot;(&quot;+r.data[0]+&quot;,&quot;+r.data[1] +&quot;) &quot;);
            preorder(r.left);             
            preorder(r.right);
        }
    }
    /* Function for postorder traversal */
    public void postorder() {
        postorder(root);
    }
    private void postorder(KDNode r) {
        if (r != null){
            postorder(r.left);             
            postorder(r.right);
            System.out.print(&quot;(&quot;+r.data[0]+&quot;,&quot;+r.data[1] +&quot;) &quot;);
        }
    }
}
