import java.util.*;

public class NumToWords {
  String string;
  String st1[] = { &quot;&quot;, &quot;one&quot;, &quot;two&quot;, &quot;three&quot;, &quot;four&quot;, &quot;five&quot;, &quot;six&quot;, &quot;seven&quot;,
                   &quot;eight&quot;, &quot;nine&quot;, };
  String st2[] = { &quot;hundred&quot;, &quot;thousand&quot;, &quot;lakh&quot;, &quot;crore&quot; };
  String st3[] = { &quot;ten&quot;, &quot;eleven&quot;, &quot;twelve&quot;, &quot;thirteen&quot;, &quot;fourteen&quot;,
                   &quot;fifteen&quot;, &quot;sixteen&quot;, &quot;seventeen&quot;, &quot;eighteen&quot;, &quot;ninteen&quot;, };
  String st4[] = { &quot;twenty&quot;, &quot;thirty&quot;, &quot;fourty&quot;, &quot;fifty&quot;, &quot;sixty&quot;, &quot;seventy&quot;,
                   &quot;eighty&quot;, &quot;ninety&quot; };

  public String convert(int number) {
    int n = 1;
    int word;
    string = &quot;&quot;;
    while (number != 0) {
      switch (n) {
        case 1:
          word = number % 100;
          pass(word);
          if (number &gt; 100 &amp;&amp; number % 100 != 0) {
            show(&quot;and &quot;);
            //System.out.print(&quot;ankit&quot;);
          }
          number /= 100;
          break;
        case 2:
          word = number % 10;
          if (word != 0) {
            show(&quot; &quot;);
            show(st2[0]);
            show(&quot; &quot;);
            pass(word);
          }
          number /= 10;
          break;
        case 3:
          word = number % 100;
          if (word != 0) {
            show(&quot; &quot;);
            show(st2[1]);
            show(&quot; &quot;);
            pass(word);
          }
          number /= 100;
          break;
        case 4:
          word = number % 100;
          if (word != 0) {
            show(&quot; &quot;);
            show(st2[2]);
            show(&quot; &quot;);
            pass(word);
          }
          number /= 100;
          break;
        case 5:
          word = number % 100;
          if (word != 0) {
            show(&quot; &quot;);
            show(st2[3]);
            show(&quot; &quot;);
            pass(word);
          }
          number /= 100;
          break;
        }
        n++;
      }
      return string;
    }
  
  public void pass(int number) {
    int word, q;
    if (number &lt; 10) {
      show(st1[number]);
    }
    if (number &gt; 9 &amp;&amp; number &lt; 20) {
      show(st3[number - 10]);
    }
    if (number &gt; 19) {
      word = number % 10;
      if (word == 0) {
        q = number / 10;
        show(st4[q - 2]);
      } else {
        q = number / 10;
        show(st1[word]);
        show(&quot; &quot;);
        show(st4[q - 2]);
      }
    }
  }

  public void show(String s) {
    String st;
    st = string;
    string = s;
    string += st;
  }

