    NumToWords w = new NumToWords();
    Scanner input = new Scanner(System.in);
    System.out.print(&quot;Enter Number: &quot;);
    int num = input.nextInt();
    String inwords = w.convert(num);
    System.out.println(inwords);
  }
}

