    fun formatDurationTime(durationSeconds: Long): String {
        var hours = 0L
        var minutes = 0L
        var seconds = durationSeconds
        if (seconds &gt;= 3600) {
            hours = seconds / 3600
            seconds -= hours * 3600
        }
        if (seconds &gt;= 60) {
            minutes = seconds / 60
            seconds -= minutes * 60
        }
