    public class ObjectMapperTest {
    
    	public static void main(String[] args) throws JsonProcessingException {
    
    		IntegerSchema schema = new IntegerSchema();
    		schema.type = &quot;Int&quot;;
    		schema.max = 10200000000d;
    		schema.min = 100d;
    
