        private void sort(AtomicInteger one, AtomicInteger two) {
            if (one.get() &gt; two.get()) {
                int tmp = one.get();
                one.set(two.get());
                two.set(tmp);
            }

            if (two == b) {
                sort(one, c);
            } else if (two == c) {
                sort(one, d);
            } else if (two == d) {
                sort(one, e);
            } else if (two == e) {
                sort(one, f);
            }
        }

