import java.io.IOException;
import java.nio.file.Path;
import java.nio.file.Paths;

public class Main {

    public static void main(String[] args) {
        
        ProcessBuilder pb = new ProcessBuilder(&quot;C:\\apache-maven-3.8.6\\bin\\mvn.cmd&quot;,
                                               &quot;archetype:generate&quot;,
