    @RequestMapping(value = &quot;/search&quot;, method = RequestMethod.GET)
    public ResponseEntity&lt;List&lt;SortParams.SortParam&gt;&gt; search(@RequestParam(&quot;sort&quot;) SortParams sort) {
    	return ResponseEntity.ok(sort.getSortParamList());
    }

