    // Will work only since Java 7 and Android API 26!
    @Suppress(&quot;NOTHING_TO_INLINE&quot;)
    inline fun loggerFactoryStatic(): Logger
        = LoggerFactory.getLogger(MethodHandles.lookup().lookupClass())


